const QUERY: &'static str = "https://figma.com";

/// bench crawling between different libs
pub fn bench_speed(c: &mut Criterion) {
    let mut group = c.benchmark_group("crawl-parse/libraries");
    let sample_count = 200;
    let sample_title = format!("parse html {} samples", sample_count);

    group.sample_size(sample_count);

    let rt = spider::tokio::runtime::Runtime::new().unwrap();

    let selectors = spider::page::get_page_selectors(&QUERY, false, false);
    let page = build(QUERY, HTML.to_string());

    group.bench_function(format!("Parse links ego: {}", sample_title), |b| {
        b.to_async(&rt)
            .iter(|| black_box(page.links_ego(&selectors)))
    });

    group.bench_function(format!("Parse links stream: {}", sample_title), |b| {
        b.to_async(&rt)
            .iter(|| black_box(page.links(&selectors, None)))
    });

    group.bench_function(
        format!("Parse links scraper select: {}", sample_title),
        |b| b.iter(|| black_box(select_fragment(&selectors.0))),
    );

    group.finish();
}

/// select fragments to items
#[inline(always)]
fn select_fragment(selector: &Selector) -> HashSet<CaseInsensitiveString> {
    let html = Html::parse_fragment(HTML);
    let items = html.select(&selector);
    let mut map: HashSet<CaseInsensitiveString> = HashSet::new();

    for item in items {
        match item.value().attr("href") {
            Some(href) => {
                map.insert(href.into());
            }
            None => {}
        }
    }

    map
}
criterion_group!(benches, bench_speed);
criterion_main!(benches);
